import './Expence.css'
import Card from './UI/Card';
import ExpenceItem from './ExpenceItem';
import ExpenceFilter from './ExpenceFilter';

function Expence(props) {
    // const [expenceyear, setexpenceyear] = new useState("2022");
    const filterechangeHanler = (selectedyear) => {
        props.onChangeFilter(selectedyear)
        // setexpenceyear(selectedyear);
    }
    // props.newExpenseYear(filterechangeHanler);
    const filteredExpense = props.expenses.filter((expense) => expense.date.toLocaleString('default', { year: 'numeric' }) === props.newExpenseYear);
    const list = filteredExpense.map((expence) => <ExpenceItem record={expence} key={expence.id} />);

    return <Card className="expenses">
        <ExpenceFilter selected={props.newExpenseYear} onChange={filterechangeHanler} />
        {list}
    </Card>
}

export default Expence;
