import { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";

import Expence from "../components/Expences/Expence";
import NewExpense from "../components/Expences/NewExpense";
import { ExpencesAction } from "../store/expences-slice";

const DUMMY_EXPENSES = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

function HomePage() {
  const [expenses, updateExpence] = new useState(DUMMY_EXPENSES);
  const Expences = useSelector((state) => state.expences);
  const dispatch = useDispatch();

  useEffect(() => {
    const FetchExpences = async () => {
      return await fetch(
        `https://expencetracker-b3897-default-rtdb.firebaseio.com/users/${Expences.userID}.json`
      );
    };
    FetchExpences().then((res) => {
      res.json().then((data) => {
        debugger;
        delete data.email;
        dispatch(ExpencesAction.updateExpence(data));
        // updateExpence(Expences.expences)
      });
    });
  }, [Expences, dispatch, updateExpence]);

  const expense = function addExpence(newexpence) {
    // DUMMY_EXPENSES.push(newexpence)
    updateExpence((previousExpence) => {
      return [newexpence, ...previousExpence];
    });
  };

  return (
    <div>
      <NewExpense addExpence={expense} />
      <Expence expenses={expenses} />
    </div>
  );
}

export default HomePage;
