import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  id: null,
  title: null,
  amount: null,
  date: null,
};
const ExpencesSlice = createSlice({
  name: "Expences",
  initialState,
  reducers: {
    add(expence) {},
    filter(year) {},
  },
});

export const AddExpence = (expence) => {
  // const ctx = useContext(AuthContext);
  // debugger
  return async () => {
    // console.log(ctx.email);
    const data = await fetch(
      `https://expencetracker-b3897-default-rtdb.firebaseio.com/users.json?"email"&equalTo${localStorage.getItem("email")}`
    );
    data.json().then(async (data) => {
      let userId = null;
      if (!data) {
        const user = await fetch(
          "https://expencetracker-b3897-default-rtdb.firebaseio.com/users.json",
          {
            method: "post",
            body: JSON.stringify({
              email: localStorage.getItem("email"),
            }),
            headers: {
              "Content-Type": "Application/Json",
            },
          }
        );
        const id = user.json().then((user) => {
          return user["name"];
        });
        userId = id;
      } else {
        userId = Object.keys(data)[0];
      }

      fetch(
        `https://expencetracker-b3897-default-rtdb.firebaseio.com/users/${userId}.json`,
        {
          method: "post",
          body: JSON.stringify(expence),
          headers: {
            "Content-Type": "Application/Json",
          },
        }
      );
    });
    debugger;
  };
};

export default ExpencesSlice;
